CREATE EXTENSION IF NOT EXISTS postgis;

create table source
(
    id       integer generated by default as identity
        primary key,
    name     varchar(255) not null,
    geojson  json,
    geometry geometry
);

create table map_style
(
    id     integer generated by default as identity
        primary key,
    name   varchar(255) not null,
    styles json
);

create table configuration
(
    name        varchar(20) not null
        constraint configuration_pk
            primary key,
    value       text,
    "group"     varchar(50),
    description varchar(150),
    multi_line  boolean
);

CREATE INDEX layer_spatial_idx ON source USING gist (geometry);

create table sprite
(
    id   integer generated by default as identity
        primary key,
    name varchar(100),
    url  varchar(300)
);

create table sprite_image
(
    id        bigserial
        constraint idx_45430_primary
        primary key,
    sprite_id integer
        constraint sprite_image_ibfk_1
            references sprite
            on update cascade on delete cascade,
    name      varchar(100),
    type      varchar(30),
    path      varchar(200)
);

create index idx_sprite_id on sprite_image (sprite_id);


CREATE OR REPLACE FUNCTION get_source_mvt(
    z integer,
    x integer,
    y integer,
    params json DEFAULT '{}'::json
) RETURNS bytea AS $$
DECLARE
    mvt bytea;
query text;
filter_conditions text := 'TRUE';
BEGIN
    -- Build filter conditions based on parameters
    IF params::jsonb ? 'id' THEN
        filter_conditions := filter_conditions || ' AND id = ANY (' ||
            'string_to_array(' || quote_literal(params->>'id') || ', '','')::int[])';
END IF;

    -- If more filter parameters are provided, add them
IF params::jsonb ? 'name' THEN
        filter_conditions := filter_conditions || ' AND name ILIKE ' ||
            quote_literal(params->>'name');
END IF;

    -- Construct and execute the query
query := format(
        'WITH mvt_geom AS (
            SELECT ST_AsMVTGeom(
                ST_Transform(geometry, 3857),  -- Transform to Web Mercator
                ST_TileEnvelope(%s, %s, %s),
                extent => 4096,
                buffer => 256
            ) AS geom,
            name,
            id
            FROM source
            WHERE ST_Transform(geometry, 3857) && ST_TileEnvelope(%s, %s, %s)
            AND %s
        )
        SELECT ST_AsMVT(q.*, q.name, 4096) FROM mvt_geom q',
        z, x, y, z, x, y, filter_conditions
    );

EXECUTE query INTO mvt;
RETURN mvt;
END;
$$ LANGUAGE plpgsql STABLE STRICT PARALLEL SAFE;


create table font
(
    id   integer generated by default as identity
        primary key,
    filename varchar(100)
);

alter table font owner to postgres;





